#include "gnl_tester.h"

int	big_line(void)
{
	int	res;
	int	fd;

	fd = open(PATH_NAME"big_line.txt", O_RDONLY);
	if (fd <= 0)
		write(1, "fail to open file\n", 18);
	res = test_compare(fd, "");
	if (res == -1)
		return (-1);
	return (0);
}
